import { StatusBar } from "expo-status-bar";
import {
  Platform,
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ScrollView,
  KeyboardAvoidingView,
} from "react-native";
import AppLoading from "expo-app-loading";
import { SafeAreaView } from "react-native-safe-area-context";
import {
  useFonts,
  RobotoCondensed_300Light,
  RobotoCondensed_300Light_Italic,
  RobotoCondensed_400Regular,
  RobotoCondensed_400Regular_Italic,
  RobotoCondensed_700Bold,
  RobotoCondensed_700Bold_Italic,
} from "@expo-google-fonts/dev";

export default function Logon() {
  let [fontsLoaded] = useFonts({
    RobotoCondensed_400Regular,
  });

  if (!fontsLoaded) {
    return <AppLoading />;
  } else {
    return (
      <SafeAreaView style={styles.container}>
        <ScrollView>
          <KeyboardAvoidingView style={{ paddingTop: "40%" }}>
            <Text style={styles.text}>
              Enter a Username and Password {"\n"} to make an account
            </Text>
            <KeyboardAvoidingView style={{ paddingLeft: "15%" }}>
              <TextInput style={styles.textbox} placeholder="Email" />
              <TextInput
                secureTextEntry={true}
                style={styles.textbox}
                placeholder="Password"
              />
              <TextInput
                secureTextEntry={true}
                style={styles.textbox}
                placeholder="Confirm Password"
              />
            </KeyboardAvoidingView>
            <TouchableOpacity
              style={styles.buttonContainer}
              onPress={() => console.log("Button Pressed")}
            >
              <Text
                style={{
                  textAlign: "center",
                  color: "white",
                  fontFamily: "RobotoCondensed_400Regular",
                }}
              >
                Login
              </Text>
            </TouchableOpacity>
          </KeyboardAvoidingView>
        </ScrollView>

        <StatusBar style="auto" />
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
    alignContent: "center",
  },
  text: {
    fontSize: 20,
    textAlign: "center",
    fontFamily: "RobotoCondensed_400Regular",
  },
  textbox: {
    backgroundColor: "white",
    height: 40,
    width: "75%",
    margin: 10,
    borderRadius: 5,
    fontFamily: "RobotoCondensed_400Regular",
    shadowColor: "black",
    shadowOpacity: 0.3,
    shadowRadius: 8,
    shadowOffset: { height: 1, width: 0.3 },
  },
  buttonContainer: {
    backgroundColor: "#22CA7B",
    width: "45%",
    alignSelf: "center",
    borderRadius: 20,
    padding: 10,
  },
});
